{"changed":true,"filter":false,"title":"post.rb","tooltip":"/app/models/post.rb","value":"# == Schema Information\n#\n# Table name: posts\n#\n#  id         :integer          not null, primary key\n#  content    :text\n#  name       :string\n#  picture    :string\n#  title      :string\n#  created_at :datetime         not null\n#  updated_at :datetime         not null\n#  user_id    :integer\n#\n\nclass Post < ApplicationRecord\n    #Commentモデルを複数持てる様にする\n    has_many :posts, dependent: :destroy\n    has_many :comments, dependent: :delete_all\n    has_many :post_tag_relations, dependent: :delete_all, foreign_key: 'post_id'\n    has_many :tags, through: :post_tag_relations  #追加\n    belongs_to :user\n    validates :user_id, presence: true\n    default_scope -> { order(created_at: :desc) }\n    # presence -> 必須チェック\n    # length -> 文字数の制限\n    # validates :name, presence: true, length: { maximum: 30 }\n    validates :title, presence: true, length: { maximum: 30 }\n    validates :content, presence: true, length: { maximum: 1000 }\n    validate  :picture_size\n    \n    #画像のアップロード機能を持たせる\n    mount_uploader :picture, PictureUploader\n    validates :user, presence:true\n    \n    #favoriteモデル追加\n    has_many :favorites, dependent: :destroy\n    has_many :users, through: :favorites\n    validates :user_id,presence: true\n    validates :content, presence: true, length: { maximum: 140 }\n    \n    private\n    \n    # アップロードされた画像のサイズをバリデーションする\n    def picture_size\n      if picture.size > 5.megabytes\n        errors.add(:picture, \"should be less than 5MB\")\n      end\n    end\n    \n    \n    \nend\n","undoManager":{"mark":28,"position":42,"stack":[[{"start":{"row":30,"column":4},"end":{"row":30,"column":20},"action":"insert","lines":["acts_as_taggable"],"id":1}],[{"start":{"row":29,"column":0},"end":{"row":29,"column":2},"action":"insert","lines":["  "],"id":2}],[{"start":{"row":29,"column":2},"end":{"row":29,"column":4},"action":"insert","lines":["  "],"id":3}],[{"start